public class LinkChickenRenderTextures
{
    [MenuItem("Tools/Link Chicken RenderTextures")]
    static void Link()
    {
        // Load RenderTextures
        RenderTexture winRT = AssetDatabase.LoadAssetAtPath<RenderTexture>("Assets/RenderTextures/WinChickenRT.renderTexture");
        RenderTexture loseRT = AssetDatabase.LoadAssetAtPath<RenderTexture>("Assets/RenderTextures/LoseChickenRT.renderTexture");

        if (winRT == null || loseRT == null)
        {
            Debug.LogError("RenderTextures not found! Make sure they exist in Assets/RenderTextures/");
            return;
        }

        // Find and link Win screen
        GameObject winDisplay = GameObject.Find("Canvas/VictoryPanel/WinChickenDisplay");
        if (winDisplay != null)
        {
            RawImage rawImage = winDisplay.GetComponent<RawImage>();
            if (rawImage != null)
            {
                rawImage.texture = winRT;
                EditorUtility.SetDirty(winDisplay);
                Debug.Log("Linked WinChickenRT to VictoryPanel");
            }
        }

        // Find and link Lose screen
        GameObject loseDisplay = GameObject.Find("Canvas/LosePanel/LoseChickenDisplay");
        if (loseDisplay != null)
        {
            RawImage rawImage = loseDisplay.GetComponent<RawImage>();
            if (rawImage != null)
            {
                rawImage.texture = loseRT;
                EditorUtility.SetDirty(loseDisplay);
                Debug.Log("Linked LoseChickenRT to LosePanel");
            }
        }

        // Link cameras to RenderTextures
        GameObject winCam = GameObject.Find("WinChickenScene/WinChickenCamera");
        if (winCam != null)
        {
            Camera cam = winCam.GetComponent<Camera>();
            if (cam != null)
            {
                cam.targetTexture = winRT;
                EditorUtility.SetDirty(winCam);
                Debug.Log("Linked WinChickenCamera to WinChickenRT");
            }
        }

        GameObject loseCam = GameObject.Find("LoseChickenScene/LoseChickenCamera");
        if (loseCam != null)
        {
            Camera cam = loseCam.GetComponent<Camera>();
            if (cam != null)
            {
                cam.targetTexture = loseRT;
                EditorUtility.SetDirty(loseCam);
                Debug.Log("Linked LoseChickenCamera to LoseChickenRT");
            }
        }

        Debug.Log("All RenderTextures linked successfully!");
        UnityEditor.SceneManagement.EditorSceneManager.MarkSceneDirty(UnityEditor.SceneManagement.EditorSceneManager.GetActiveScene());
    }
}

using UnityEngine;
using UnityEngine.UI;

/// <summary>
/// Sets up RenderTextures for Win/Lose chicken displays at runtime.
/// Creates RenderTextures dynamically and assigns them to cameras and UI.
/// </summary>
public class ChickenRenderTextureSetup : MonoBehaviour
{
    [Header("Cameras")]
    [SerializeField] private Camera winChickenCamera;
    [SerializeField] private Camera loseChickenCamera;

    [Header("UI RawImages")]
    [SerializeField] private RawImage winChickenDisplay;
    [SerializeField] private RawImage loseChickenDisplay;

    [Header("Settings")]
    [SerializeField] private int textureSize = 512;
    [SerializeField] private int antiAliasing = 4;

    private RenderTexture winRT;
    private RenderTexture loseRT;

    void Awake()
    {
        SetupRenderTextures();
    }

    void SetupRenderTextures()
    {
        // Create Win RenderTexture
        winRT = new RenderTexture(textureSize, textureSize, 24);
        winRT.name = "WinChickenRT";
        winRT.antiAliasing = antiAliasing;

        // Create Lose RenderTexture
        loseRT = new RenderTexture(textureSize, textureSize, 24);
        loseRT.name = "LoseChickenRT";
        loseRT.antiAliasing = antiAliasing;

        // Assign to cameras
        if (winChickenCamera != null)
        {
            winChickenCamera.targetTexture = winRT;
        }
        else
        {
            Debug.LogWarning("WinChickenCamera not assigned!");
        }

        if (loseChickenCamera != null)
        {
            loseChickenCamera.targetTexture = loseRT;
        }
        else
        {
            Debug.LogWarning("LoseChickenCamera not assigned!");
        }

        // Assign to UI
        if (winChickenDisplay != null)
        {
            winChickenDisplay.texture = winRT;
        }
        else
        {
            Debug.LogWarning("WinChickenDisplay not assigned!");
        }

        if (loseChickenDisplay != null)
        {
            loseChickenDisplay.texture = loseRT;
        }
        else
        {
            Debug.LogWarning("LoseChickenDisplay not assigned!");
        }

        Debug.Log("Chicken RenderTextures setup complete!");
    }

    void OnDestroy()
    {
        // Clean up RenderTextures
        if (winRT != null)
        {
            winRT.Release();
            Destroy(winRT);
        }

        if (loseRT != null)
        {
            loseRT.Release();
            Destroy(loseRT);
        }
    }
}

using UnityEngine;
using System.Collections;

namespace LexiRun.UI
{
    /// <summary>
    /// Base class for UI animations. Provides common animation utilities.
    /// </summary>
    public class UIAnimator : MonoBehaviour
    {
        [Header("Animation Settings")]
        [SerializeField] protected bool playOnEnable = true;
        [SerializeField] protected float delay = 0f;

        protected RectTransform rectTransform;
        protected Vector3 originalScale;
        protected Vector3 originalPosition;
        protected Quaternion originalRotation;

        protected virtual void Awake()
        {
            rectTransform = GetComponent<RectTransform>();
            if (rectTransform != null)
            {
                originalScale = rectTransform.localScale;
                originalPosition = rectTransform.localPosition;
                originalRotation = rectTransform.localRotation;
            }
        }

        protected virtual void OnEnable()
        {
            if (playOnEnable)
            {
                if (delay > 0)
                {
                    StartCoroutine(DelayedPlay());
                }
                else
                {
                    PlayAnimation();
                }
            }
        }

        protected virtual void OnDisable()
        {
            StopAllCoroutines();
            ResetToOriginal();
        }

        IEnumerator DelayedPlay()
        {
            yield return new WaitForSeconds(delay);
            PlayAnimation();
        }

        public virtual void PlayAnimation()
        {
            // Override in derived classes
        }

        public virtual void StopAnimation()
        {
            StopAllCoroutines();
            ResetToOriginal();
        }

        protected virtual void ResetToOriginal()
        {
            if (rectTransform != null)
            {
                rectTransform.localScale = originalScale;
                rectTransform.localPosition = originalPosition;
                rectTransform.localRotation = originalRotation;
            }
        }
    }
}

using UnityEngine;
using System.Collections;

namespace LexiRun.UI
{
    /// <summary>
    /// Animation #1: Ping-pong scale animation (breathing effect).
    /// Smoothly scales between min and max values.
    /// </summary>
    public class UIPingPongScale : UIAnimator
    {
        [Header("Ping-Pong Settings")]
        [SerializeField] private float minScale = 0.95f;
        [SerializeField] private float maxScale = 1.05f;
        [SerializeField] private float speed = 1f;
        [SerializeField] private AnimationCurve curve = AnimationCurve.EaseInOut(0, 0, 1, 1);

        public override void PlayAnimation()
        {
            StopAllCoroutines();
            StartCoroutine(PingPongScaleRoutine());
        }

        IEnumerator PingPongScaleRoutine()
        {
            while (true)
            {
                float t = 0f;

                // Scale up
                while (t < 1f)
                {
                    t += Time.deltaTime * speed;
                    float scale = Mathf.Lerp(minScale, maxScale, curve.Evaluate(t));
                    rectTransform.localScale = originalScale * scale;
                    yield return null;
                }

                t = 0f;

                // Scale down
                while (t < 1f)
                {
                    t += Time.deltaTime * speed;
                    float scale = Mathf.Lerp(maxScale, minScale, curve.Evaluate(t));
                    rectTransform.localScale = originalScale * scale;
                    yield return null;
                }
            }
        }
    }
}

using UnityEngine;
using System.Collections;

namespace LexiRun.UI
{
    /// <summary>
    /// Animation #2: Jump scale animation (attention-grabbing bounce).
    /// Scales up and down in a bouncy motion.
    /// </summary>
    public class UIJumpScale : UIAnimator
    {
        [Header("Jump Settings")]
        [SerializeField] private float jumpScale = 1.2f;
        [SerializeField] private float jumpDuration = 0.3f;
        [SerializeField] private float pauseDuration = 1f;
        [SerializeField] private AnimationCurve jumpCurve = AnimationCurve.EaseInOut(0, 0, 1, 1);

        public override void PlayAnimation()
        {
            StopAllCoroutines();
            StartCoroutine(JumpScaleRoutine());
        }

        IEnumerator JumpScaleRoutine()
        {
            while (true)
            {
                // Jump up
                yield return StartCoroutine(ScaleToRoutine(jumpScale, jumpDuration * 0.4f));

                // Jump down
                yield return StartCoroutine(ScaleToRoutine(1f, jumpDuration * 0.6f));

                // Pause
                yield return new WaitForSeconds(pauseDuration);
            }
        }

        IEnumerator ScaleToRoutine(float targetScale, float duration)
        {
            Vector3 startScale = rectTransform.localScale;
            Vector3 endScale = originalScale * targetScale;
            float t = 0f;

            while (t < 1f)
            {
                t += Time.deltaTime / duration;
                rectTransform.localScale = Vector3.Lerp(startScale, endScale, jumpCurve.Evaluate(t));
                yield return null;
            }

            rectTransform.localScale = endScale;
        }
    }
}

