using UnityEngine;
using UnityEngine.UI;
using System.Collections;

namespace LexiRun.UI
{
    /// <summary>
    /// Animation #7: Timer flash animation (urgency indicator).
    /// Flashes red when timer is low.
    /// </summary>
    public class UITimerFlash : MonoBehaviour
    {
        [Header("Flash Settings")]
        [SerializeField] private float warningThreshold = 10f;
        [SerializeField] private Color normalColor = Color.white;
        [SerializeField] private Color warningColor = Color.red;
        [SerializeField] private float flashSpeed = 2f;

        [Header("References")]
        [SerializeField] private Text timerText;
        [SerializeField] private Image timerImage;

        private PlayerController player;
        private bool isFlashing = false;

        void Start()
        {
            if (timerText == null)
            {
                timerText = GetComponent<Text>();
            }
        }

        void Update()
        {
            // Find player if not found
            if (player == null)
            {
                player = FindObjectOfType<PlayerController>();
                if (player == null) return;
            }

            // Check if timer is low
            bool shouldFlash = player.currentTime <= warningThreshold && player.currentTime > 0;

            if (shouldFlash && !isFlashing)
            {
                StartFlashing();
            }
            else if (!shouldFlash && isFlashing)
            {
                StopFlashing();
            }

            // Apply flash effect
            if (isFlashing)
            {
                float t = (Mathf.Sin(Time.time * flashSpeed * Mathf.PI) + 1f) * 0.5f;
                Color flashColor = Color.Lerp(normalColor, warningColor, t);

                if (timerText != null)
                {
                    timerText.color = flashColor;
                }

                if (timerImage != null)
                {
                    timerImage.color = flashColor;
                }
            }
        }

        void StartFlashing()
        {
            isFlashing = true;
        }

        void StopFlashing()
        {
            isFlashing = false;

            if (timerText != null)
            {
                timerText.color = normalColor;
            }

            if (timerImage != null)
            {
                timerImage.color = normalColor;
            }
        }
    }
}

using UnityEngine;
using UnityEngine.UI;
using System.Collections;

namespace LexiRun.UI
{
    /// <summary>
    /// Animation #8: HP pulse animation (damage feedback).
    /// Pulses and shakes when HP decreases.
    /// </summary>
    public class UIHPPulse : MonoBehaviour
    {
        [Header("Pulse Settings")]
        [SerializeField] private float pulseDuration = 0.3f;
        [SerializeField] private float pulseScale = 1.3f;
        [SerializeField] private float shakeIntensity = 10f;
        [SerializeField] private Color damageColor = Color.red;

        [Header("References")]
        [SerializeField] private Image hpImage;

        private RectTransform rectTransform;
        private Vector3 originalScale;
        private Vector3 originalPosition;
        private Color originalColor;
        private PlayerController player;
        private float lastHP = -1f;

        void Awake()
        {
            rectTransform = GetComponent<RectTransform>();
            originalScale = rectTransform.localScale;
            originalPosition = rectTransform.localPosition;

            if (hpImage != null)
            {
                originalColor = hpImage.color;
            }
        }

        void Update()
        {
            // Find player if not found
            if (player == null)
            {
                player = FindObjectOfType<PlayerController>();
                if (player == null) return;
            }

            // Initialize last HP
            if (lastHP < 0)
            {
                lastHP = player.currentHP;
                return;
            }

            // Check if HP decreased
            if (player.currentHP < lastHP)
            {
                TriggerPulse();
                lastHP = player.currentHP;
            }
        }

        public void TriggerPulse()
        {
            StopAllCoroutines();
            StartCoroutine(PulseRoutine());
        }

        IEnumerator PulseRoutine()
        {
            float t = 0f;

            while (t < 1f)
            {
                t += Time.deltaTime / pulseDuration;

                // Scale pulse
                float scale = 1f;
                if (t < 0.3f)
                {
                    scale = Mathf.Lerp(1f, pulseScale, t / 0.3f);
                }
                else
                {
                    scale = Mathf.Lerp(pulseScale, 1f, (t - 0.3f) / 0.7f);
                }
                rectTransform.localScale = originalScale * scale;

                // Shake
                float shake = Mathf.Sin(t * 20f) * shakeIntensity * (1f - t);
                rectTransform.localPosition = originalPosition + new Vector3(shake, 0, 0);

                // Color flash
                if (hpImage != null)
                {
                    hpImage.color = Color.Lerp(damageColor, originalColor, t);
                }

                yield return null;
            }

            rectTransform.localScale = originalScale;
            rectTransform.localPosition = originalPosition;

            if (hpImage != null)
            {
                hpImage.color = originalColor;
            }
        }
    }
}

