            }

#elif (UNITY_EDITOR)
            isVersionSupported = true;
#endif
        }

        /// <summary>
        /// Function that initializes the rest of the DeviceCapabilities properties.
        /// Must be called after <c>LofeltHaptics</c> was initialized.
        /// </summary>
        public static void Init()
        {
