    [SerializeField] private string sortingLayerName = "Default";
    
    void Start()
    {
        FixRendering();
    }
    
    void FixRendering()
    {
        MeshRenderer meshRenderer = GetComponent<MeshRenderer>();
        
        if (meshRenderer != null)
        {
            // Set sorting layer and order
            meshRenderer.sortingLayerName = sortingLayerName;
            meshRenderer.sortingOrder = sortingOrder;
            
            // Ensure material respects depth
            if (meshRenderer.material != null)
            {
                // Set render queue to Geometry (2000) instead of Transparent (3000)
                // This makes it render with depth testing enabled
                meshRenderer.material.renderQueue = 2000;
            }
        }
    }
    
    #if UNITY_EDITOR
    void OnValidate()
    {
        // Apply fix in editor when values change
        if (Application.isPlaying)
        {
            FixRendering();
        }
    }
    #endif
}

