using UnityEngine;
using UnityEditor;
using UnityEngine.UI;

public class FinalBotInfoUISetup
{
    [MenuItem("Tools/Setup BotInfoUI Prefab")]
    public static void SetupPrefab()
    {
        string prefabPath = "Assets/Prefabs/BotInfoUI.prefab";

        // Load prefab contents
        GameObject prefabContents = PrefabUtility.LoadPrefabContents(prefabPath);

        try
        {
            // Remove existing BotCurrentWordProgressText if it exists
            Transform existing = prefabContents.transform.Find("BotCurrentWordProgressText");
            if (existing != null)
            {
                Object.DestroyImmediate(existing.gameObject);
            }

            // Create new text object
            GameObject textObj = new GameObject("BotCurrentWordProgressText");
            textObj.transform.SetParent(prefabContents.transform, false);

            // Setup RectTransform
            RectTransform rt = textObj.AddComponent<RectTransform>();
            rt.anchorMin = new Vector2(0, 0);
            rt.anchorMax = new Vector2(1, 1);
            rt.sizeDelta = new Vector2(0, 30);
            rt.anchoredPosition = Vector2.zero;

            // Setup Text component
            Text text = textObj.AddComponent<Text>();
            text.text = "Current: 0/5";
            text.fontSize = 12;
            text.color = Color.white;
            text.alignment = TextAnchor.MiddleLeft;

            // Copy font from BotMistakesText
            Transform mistakesText = prefabContents.transform.Find("BotMistakesText");
            if (mistakesText != null)
            {
                Text mistakesTextComp = mistakesText.GetComponent<Text>();
                if (mistakesTextComp != null && mistakesTextComp.font != null)
                {
                    text.font = mistakesTextComp.font;
                    text.fontSize = mistakesTextComp.fontSize;
                }

                // Place after BotMistakesText
                textObj.transform.SetSiblingIndex(mistakesText.GetSiblingIndex() + 1);
            }

            // Add or get BotInfoUI component
            BotInfoUI botInfoUI = prefabContents.GetComponent<BotInfoUI>();
            if (botInfoUI == null)
            {
                botInfoUI = prefabContents.AddComponent<BotInfoUI>();
            }

            // Assign all references
            botInfoUI.botNameText = prefabContents.transform.Find("BotNameText")?.GetComponent<Text>();
            botInfoUI.botWordText = prefabContents.transform.Find("BotWordText")?.GetComponent<Text>();
            botInfoUI.botMistakesText = prefabContents.transform.Find("BotMistakesText")?.GetComponent<Text>();
            botInfoUI.botWordsCompletedText = prefabContents.transform.Find("BotWordsCompletedText")?.GetComponent<Text>();
            botInfoUI.botCurrentWordProgressText = textObj.GetComponent<Text>();

            // Save
            PrefabUtility.SaveAsPrefabAsset(prefabContents, prefabPath);

            Debug.Log("BotInfoUI prefab setup complete!");
        }
        finally
        {
            PrefabUtility.UnloadPrefabContents(prefabContents);
        }

        AssetDatabase.Refresh();
    }
}

using UnityEngine;
using UnityEditor;

public class InvokeBotInfoUISetup : MonoBehaviour
{
    public static string Execute()
    {
        FinalBotInfoUISetup.SetupPrefab();
        return "BotInfoUI prefab setup invoked successfully!";
    }
}

using UnityEngine;
using UnityEngine.UI;

public class BotInfoUI : MonoBehaviour
{
    public Text botNameText;
    public Text botWordText;
    public Text botMistakesText;
    public Text botWordsCompletedText;
    public Text botCurrentWordProgressText;

    private BotController bot;

    public void Initialize(BotController bot)
    {
        this.bot = bot;

        if (botNameText != null)
        {
            botNameText.text = bot.actorName;
            botNameText.color = bot.actorColor;
        }
    }

    public void UpdateInfo()
    {
        if (bot == null) return;

        if (bot.isEliminated)
        {
            if (botWordText != null) botWordText.text = "ELIMINATED";
            if (botMistakesText != null) botMistakesText.text = "";
            if (botWordsCompletedText != null) botWordsCompletedText.text = "";
            if (botCurrentWordProgressText != null) botCurrentWordProgressText.text = "";
            return;
        }

        // Hide word text since it's now floating above the bot
        if (botWordText != null)
        {
            botWordText.text = "";
        }

        if (botMistakesText != null)
        {
            botMistakesText.text = "Mistakes: " + bot.mistakeCount + "/3";
        }

        if (botWordsCompletedText != null)
        {
            botWordsCompletedText.text = "Words: " + bot.completedWords + "/3";
        }

        // Display current word progress (e.g., "Current: 2/5")
        if (botCurrentWordProgressText != null && bot.wordProgress != null)
        {
            int currentProgress = bot.wordProgress.GetProgress();
            int totalLetters = bot.wordProgress.currentWord != null ? bot.wordProgress.currentWord.Length : 0;
            botCurrentWordProgressText.text = "Current: " + currentProgress + "/" + totalLetters;
        }
    }
}

